import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class UserMessage extends Component {
  render() {
    const { userName = "Гость" } = this.props;
    return <p className="user-message">Пользователь: {userName}</p>;
  }
}

class NavigationBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentDate: null,
      userTimezone: "",
      currentUser: null,
    };
  }

  componentDidMount() {
    const localDate = new Date();
    this.setState({
      currentDate: localDate,
      userTimezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    });

    this.fetchCurrentUserData();
  }

  componentDidUpdate(prevProps) {
    if (this.props.isLoggedIn && this.props.isLoggedIn !== prevProps.isLoggedIn) {
      this.fetchCurrentUserData();
    }
  }

  fetchCurrentUserData = () => {
    axios
      .get("http://localhost:2000/api/auth/user", { withCredentials: true })
      .then((response) => {
        const { userName } = response.data;
        this.setState({ currentUser: userName });
      })
      .catch((error) => {
        console.error("NavigationBar: ошибка при получении данных пользователя:", error);
      });
  };

  handleSignOut = () => {
    axios.post("http://localhost:2000/api/auth/logout")
      .then(() => {
        this.props.onSignOut();
        this.setState({ currentUser: null }); // Сбрасываем имя пользователя при выходе
      })
      .catch(error => console.error("NavigationBar: ошибка выхода:", error));
  };

  render() {
    const { isLoggedIn } = this.props;
    const { currentDate, userTimezone, currentUser } = this.state;

    return (
      <header className="navigation-bar">
        <nav className="navigation">

          <div className="nav-links">
            <Link to="/api">Погода и цитаты</Link>
            <Link to="/catalog">Каталог</Link>
            {isLoggedIn ? (
              <>
                <Link to="/genres">Категории</Link>
                <Link to="/movies">Фильмы</Link>
                <Link to="/moviesessions">Сеансы</Link>
                <button onClick={this.handleSignOut} className="signout-button">Выйти</button>
              </>
            ) : (
              <>
                <Link to="/login">Войти</Link>
                <Link to="/register">Регистрация</Link>
              </>
            )}
          </div>

          <div className="user-info">
            {currentDate && (
              <p className="date-info">
                Дата: {currentDate.toLocaleDateString()} (Часовой пояс: {userTimezone})
              </p>
            )}
            <UserMessage userName={isLoggedIn ? currentUser : undefined} />
          </div>
        </nav>
      </header>
    );
  }
}

export default NavigationBar;
