// src/MovieSessionService.js
import axios from "axios";

const API_URL = "http://localhost:2000/api/movieSessions"; // Замените на ваш API URL

class MovieSessionService {
  // Получить все сеансы
  getAll() {
    return axios.get(API_URL);
  }

  // Получить сеанс по ID
  get(id) {
    return axios.get(`${API_URL}/${id}`);
  }

  // Создать новый сеанс
  create(data) {
    return axios.post(API_URL, data);
  }

  // Обновить сеанс по ID
  update(id, data) {
    return axios.put(`${API_URL}/${id}`, data);
  }

  // Удалить сеанс по ID
  delete(id) {
    return axios.delete(`${API_URL}/${id}`);
  }

  // Удалить все сеансы
  deleteAll() {
    return axios.delete(API_URL);
  }

  // Поиск сеанса по времени (например, по времени начала)
  searchByTime(sessionTime) {
    return axios.get(`${API_URL}?sessionTime=${encodeURIComponent(sessionTime)}`);
  }

  // Поиск сеанса по названию фильма
  searchByMovieName(movieName) {
    return axios.get(`${API_URL}?movieName=${encodeURIComponent(movieName)}`);
  }
}

// Создаем экземпляр класса и экспортируем его
const movieSessionServiceInstance = new MovieSessionService();
export default movieSessionServiceInstance;
