import React, { useState, useEffect } from "react";
import MovieSessionService from "../services/MovieSessionService"; // Сервис для работы с сеансами

const SessionCatalog = () => {
  const [sessions, setSessions] = useState([]);
  const [searchTerm, setSearchTerm] = useState(""); // Состояние для строки поиска по дате
  const [sortOrder, setSortOrder] = useState("asc"); // Состояние для сортировки по цене

  useEffect(() => {
    retrieveSessions();
  }, []);

  const retrieveSessions = () => {
    MovieSessionService.getAll()
      .then(response => {
        setSessions(response.data);
      })
      .catch(e => {
        console.error(e);
      });
  };

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    if (term) {
      // Фильтрация по дате
      MovieSessionService.searchByDate(term) // Здесь передаем строку даты в запрос
        .then(response => {
          setSessions(response.data);
        })
        .catch(e => {
          console.error(e);
        });
    } else {
      retrieveSessions(); // Если строка поиска пуста, возвращаем все данные
    }
  };

  const sortSessions = (sessions) => {
    return [...sessions].sort((a, b) => {
      // Сортировка по цене
      return sortOrder === "asc" ? a.price - b.price : b.price - a.price;
    });
  };

  return (
    <div>
      <h1>Каталог сеансов</h1>

      {/* Поле для поиска по дате */}
      <div>
        <input
          type="text"
          placeholder="Поиск по дате (например, 2024-11-30)"
          value={searchTerm}
          onChange={handleSearchChange} // Обрабатываем изменения в поле поиска
        />
      </div>

      <button className="sort-button" onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}>
        Сортировать {sortOrder === "asc" ? "по возрастанию цены" : "по убыванию цены"}
      </button>

      <ul>
        {sortSessions(sessions).map(session => (
          <li key={session.id}>
            <div>
              <p>Фильм: {session.movie.title}</p>
              <p>Дата и время: {session.date}</p>
              <p>Цена: ${session.price}</p>
              <p>Зал: {session.cinemaHall}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SessionCatalog;
