import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import MovieSessionService from "../services/MovieSessionService";

const SessionDetails = () => {
  const { id } = useParams(); // Получаем ID сеанса из маршрута
  const [session, setSession] = useState(null);

  useEffect(() => {
    MovieSessionService.get(id)
      .then((response) => {
        setSession(response.data);
      })
      .catch((error) => {
        console.error("Error fetching session details:", error);
      });
  }, [id]);

  if (!session) {
    return <p>Загрузка...</p>;
  }

  return (
      <div>
          <h1>Детали сеанса</h1>
          <p>Название фильма: {session.movie.title}</p>
          <p>Описание: {session.movie.description}</p>
          <p>Длительность: {session.movie.duration} минут</p>
          <p>
              Жанры:{" "}
              {session.movie.genres && session.movie.genres.length > 0
                  ? session.movie.genres.map((genre) => genre.name).join(", ")
                  : "Нет жанров"}
          </p>
          <img src={session.movie.posterPath} alt={session.movie.title}/>
      </div>
  );
};

export default SessionDetails;
