import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class UserGreeting extends Component {
  render() {
    const { username = "Посетитель" } = this.props;
    return <p>Пользователь: {username}</p>;
  }
}

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userDate: null,
      userTimezone: "",
      username: null,
    };
  }

  componentDidMount() {
    const currentLocalDate = new Date();
    this.setState({
      userDate: currentLocalDate,
      userTimezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    });

    this.fetchUserData();
  }

  componentDidUpdate(prevProps) {
    if (this.props.isAuthenticated && this.props.isAuthenticated !== prevProps.isAuthenticated) {
      this.fetchUserData();
    }
  }

  fetchUserData = () => {
    axios
      .get("http://localhost:2000/api/auth/user", { withCredentials: true })
      .then((response) => {
        const { username } = response.data;
        this.setState({ username });
      })
      .catch((error) => {
        console.error("Header: ошибка при получении данных пользователя:", error);
      });
  };

  handleLogout = () => {
    axios.post("http://localhost:2000/api/auth/logout")
      .then(() => {
        this.props.onLogout();
        this.setState({ username: null }); // Сбрасываем имя пользователя при выходе
      })
      .catch(error => console.error("Header: ошибка выхода:", error));
  };

  render() {
    const { isAuthenticated } = this.props;
    const { userDate, userTimezone, username } = this.state;

    return (
      <header>
        <nav>
          <Link to="/api">Погода, цитаты </Link>
          
          {isAuthenticated ? (
            <>
              <Link to="/genres">Жанры </Link>
              <Link to="/movies">Фильмы </Link>
              <Link to="/movieSessions">Сеансы </Link>
              <button onClick={this.handleLogout}>Выйти</button>
            </>
          ) : (
            <>
              <Link to="/login">Войти </Link>
              <Link to="/register">Регистрация</Link>
            </>
          )}
          <div className="header-info">
            {userDate && (
              <p>Дата: {userDate.toLocaleDateString()} (Часовой пояс: {userTimezone})</p>
            )}
            <UserGreeting username={isAuthenticated ? username : undefined} />
          </div>
        </nav>
      </header>
    );
  }
}

export default Header;
