// src/MovieService.js
import axios from "axios";

const API_URL = "http://localhost:2000/api/movies"; // Замените на ваш API URL

class MovieService {
  // Получить все фильмы
  getAll() {
    return axios.get(API_URL);
  }

  // Получить фильм по ID
  get(id) {
    return axios.get(`${API_URL}/${id}`);
  }

  // Создать новый фильм
  create(data) {
    return axios.post(API_URL, data);
  }

  // Обновить фильм по ID
  update(id, data) {
    return axios.put(`${API_URL}/${id}`, data);
  }

  // Удалить фильм по ID
  delete(id) {
    return axios.delete(`${API_URL}/${id}`);
  }

  // Удалить все фильмы
  deleteAll() {
    return axios.delete(API_URL);
  }

  // Поиск фильма по названию
  search(title) {
    return axios.get(`${API_URL}?title=${encodeURIComponent(title)}`);
  }
}

// Создаем экземпляр класса и экспортируем его
const movieServiceInstance = new MovieService();
export default movieServiceInstance;
