import React, { useEffect, useState } from "react";
import axios from "axios";

const Api = () => {
  const [weather, setWeather] = useState(null);
  const [quote, setQuote] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Запрос данных о погоде и цитате
    const fetchWeatherAndQuote = async () => {
      try {
        const [weatherResponse, quoteResponse] = await Promise.all([
          axios.get("http://localhost:2000/api/weather"),
          axios.get("http://localhost:2000/api/quote")
        ]);
        setWeather(weatherResponse.data);
        setQuote(quoteResponse.data);
      } catch (error) {
        console.error("Ошибка при получении данных:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchWeatherAndQuote().then(r => console.log(r));
  }, []);

  if (loading) return <p>Загрузка...</p>;

  return (
      <div>
        {weather && (
            <div className="weather-info">
              <h3 className="weather-title">Погода в Минске</h3>
              <div className="weather-details">
                <p className="temperature">Температура: {weather.temp} °C</p>
                <img
                    className="weather-icon"
                    src={`https://openweathermap.org/img/wn/${weather.icon}@2x.png`}
                    alt="Weather Icon"
                />
              </div>
            </div>
        )}

        {/* Отображение цитаты */}
        <div className="quote-container">
          <h3 className="quote-title">Цитата дня</h3>
          <blockquote className="quote-text">{quote.quote}</blockquote>
        </div>
      </div>
  );
};

export default Api;
