import React, { useState } from "react";
import axios from "axios";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("http://localhost:2000/api/auth/register", { username, password })
      .then(response => {
        setMessage("Регистрация успешна! Теперь войдите.");

      })
      .catch(error => {
        console.error('Ошибка регистрации:', error.response ? error.response.data : error.message);
        setMessage("Ошибка регистрации: " + (error.response && error.response.data.message ? error.response.data.message : "Неизвестная ошибка"));
      });
  };

  return (
    <div>
      <h2>Регистрация</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="add-button" type="submit">Зарегистрироваться</button>
      </form>
    </div>
  );
}

export default Register;
