// App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import axios from "axios";

import Header from "./components/Header";
import GenreList from "./components/Genres";
import MovieList from "./components/Movies";
import SessionList from "./components/MovieSessions";
import SessionCatalog from "./components/Catalog";
import SessionDetails from "./components/SessionDetails";
import Login from "./components/Login";
import Register from "./components/Register";
import Api from "./components/API";

import "./styles/style.css";

axios.defaults.withCredentials = true;

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:2000/api/auth/isAuthenticated", { withCredentials: true })
      .then(response => {
        if (response.data && typeof response.data.isAuthenticated !== "undefined") {
          setIsAuthenticated(response.data.isAuthenticated);
        } else {
          console.warn("Ответ сервера не содержит данных об аутентификации. Проверьте ответ сервера.");
        }
      })
      .catch(error => {
        console.error("Ошибка проверки сессии. Проверьте доступность сервера и передаются ли куки:", error);
      });
  }, []);

  const handleLogout = () => {
    console.log("Выполняем выход из системы...");
    setIsAuthenticated(false);
    navigate("/api");
  };

  return (
    <Router>
      <div className="App">
        <Header isAuthenticated={isAuthenticated} onLogout={handleLogout} />

        <Routes>
          <Route path="/api" element={<Api />} />
          <Route path="/catalog" element={<SessionCatalog />} />
          <Route path="/moviesession/:id" element={<SessionDetails />} />
          <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />
          <Route path="/register" element={<Register />} />

          {/* Защищенные маршруты */}
          {isAuthenticated ? (
            <>
              <Route path="/genres" element={<GenreList />} />
              <Route path="/movies" element={<MovieList />} />
              <Route path="/moviesessions" element={<SessionList />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/api" replace />} />
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
