import React, { useState, useEffect } from "react";
import MovieService from "../services/MovieService"; // Сервис для работы с фильмами
import SessionService from "../services/MovieSessionService"; // Сервис для работы с сеансами


const SessionList = () => {
  const [sessions, setSessions] = useState([]);
  const [movies, setMovies] = useState([]); // Для выбора фильма
  const [selectedSession, setSelectedSession] = useState(null);
  const [newSession, setNewSession] = useState({
    movie: "",
    sessionTime: "",
    price: "",
    cinemaHall: "",
  });
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    retrieveSessions();
    retrieveMovies(); // Получаем список фильмов для выбора
  }, []);

  const retrieveSessions = () => {
    SessionService.getAll()
      .then((response) => {
        setSessions(response.data);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const retrieveMovies = () => {
    MovieService.getAll()
      .then((response) => {
        setMovies(response.data);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    if (term) {
      SessionService.search(term)
        .then((response) => {
          setSessions(response.data);
        })
        .catch((e) => {
          console.error(e);
        });
    } else {
      retrieveSessions();
    }
  };

  const createSession = () => {
    if (
      !newSession.movie.trim() ||
      !newSession.sessionTime ||
      !newSession.price ||
      !newSession.cinemaHall.trim()
    ) {
      alert("Пожалуйста, заполните все поля!");
      return;
    }

    if (isNaN(newSession.price) || newSession.price <= 0) {
      alert("Цена должна быть положительным числом!");
      return;
    }

    SessionService.create(newSession)
      .then(() => {
        retrieveSessions();
        setNewSession({
          movie: "",
          sessionTime: "",
          price: "",
          cinemaHall: "",
        });
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const updateSession = (id) => {
    if (
      !selectedSession.movie.trim() ||
      !selectedSession.sessionTime ||
      !selectedSession.price ||
      !selectedSession.cinemaHall.trim()
    ) {
      alert("Пожалуйста, заполните все поля!");
      return;
    }

    if (isNaN(selectedSession.price) || selectedSession.price <= 0) {
      alert("Цена должна быть положительным числом!");
      return;
    }

    SessionService.update(id, selectedSession)
      .then(() => {
        retrieveSessions();
        setSelectedSession(null);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const deleteSession = (id) => {
    SessionService.delete(id)
      .then(() => {
        retrieveSessions();
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return {
      utc: date.toUTCString(),
      local: date.toLocaleString("ru-RU"),
    };
  };

  const sortSessions = (sessions) => {
    return [...sessions].sort((a, b) => {
      return sortOrder === "asc"
        ? new Date(a.sessionTime) - new Date(b.sessionTime)
        : new Date(b.sessionTime) - new Date(a.sessionTime);
    });
  };
  if (loading) return <p>Загрузка...</p>;
  return (
    <div>
      <h1>Сеансы</h1>

      {/* Поле для поиска по названию фильма */}
      <div>
        <input
          type="text"
          placeholder="Поиск по названию"
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>

      <button
        className="sort-button"
        onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}
      >
        Сортировать {sortOrder === "asc" ? "по времени" : "по времени (обратный)"}
      </button>

      {/* Форма для добавления нового сеанса */}
      <div>
        <select
          value={newSession.movie}
          onChange={(e) => setNewSession({ ...newSession, movie: e.target.value })}
        >
          <option value="">Выберите фильм</option>
          {movies.map((movie) => (
            <option key={movie.id} value={movie.id}>
              {movie.title}
            </option>
          ))}
        </select>

        <input
          type="datetime-local"
          value={newSession.sessionTime}
          onChange={(e) => setNewSession({ ...newSession, sessionTime: e.target.value })}
        />
        <input
          type="number"
          placeholder="Цена"
          value={newSession.price}
          onChange={(e) => setNewSession({ ...newSession, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Название зала"
          value={newSession.cinemaHall}
          onChange={(e) => setNewSession({ ...newSession, cinemaHall: e.target.value })}
        />
        <button className="add-button" onClick={createSession}>Добавить</button>
      </div>

      <ul>
        {sortSessions(sessions).map((session) => {
          const createdAt = formatDateTime(session.createdAt);
          const updatedAt = formatDateTime(session.updatedAt);

          return (
            <li key={session.id}>
              {selectedSession && selectedSession.id === session.id ? (
                <div>
                  <select
                    value={selectedSession.movie}
                    onChange={(e) => setSelectedSession({ ...selectedSession, movie: e.target.value })}
                  >
                    <option value="">Выберите фильм</option>
                    {movies.map((movie) => (
                      <option key={movie.id} value={movie.id}>
                        {movie.title}
                      </option>
                    ))}
                  </select>

                  <input
                    type="datetime-local"
                    value={selectedSession.sessionTime}
                    onChange={(e) => setSelectedSession({ ...selectedSession, sessionTime: e.target.value })}
                  />
                  <input
                    type="number"
                    value={selectedSession.price}
                    onChange={(e) => setSelectedSession({ ...selectedSession, price: e.target.value })}
                  />
                  <input
                    type="text"
                    value={selectedSession.cinemaHall}
                    onChange={(e) => setSelectedSession({ ...selectedSession, cinemaHall: e.target.value })}
                  />
                  <button className="add-button" onClick={() => updateSession(session.id)}>Сохранить</button>
                </div>
              ) : (
                <div>
                  <p>Фильм: {session.movie.title}</p>
                  <p>Время сеанса: {new Date(session.sessionTime).toLocaleString("ru-RU")}</p>
                  <p>Цена: {session.price} руб</p>
                  <p>Зал: {session.cinemaHall}</p>
                  <p>Создание: {createdAt.utc} (UTC), {createdAt.local} (локальное)</p>
                  <p>Последнее обновление: {updatedAt.utc} (UTC), {updatedAt.local} (локальное)</p>
                  <button className="edit-button" onClick={() => setSelectedSession(session)}>Изменить</button>
                </div>
              )}
              <button className="delete-button" onClick={() => deleteSession(session.id)}>Удалить</button>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default SessionList;
