// src/GenreService.js
import axios from "axios";

const API_URL = "http://localhost:2000/api/genres"; // Замените на ваш API URL

class GenreService {
  // Получить все жанры
  getAll() {
    return axios.get(API_URL);
  }

  // Получить жанр по ID
  get(id) {
    return axios.get(`${API_URL}/${id}`);
  }

  // Создать новый жанр
  create(data) {
    return axios.post(API_URL, data);
  }

  // Обновить жанр по ID
  update(id, data) {
    return axios.put(`${API_URL}/${id}`, data);
  }

  // Удалить жанр по ID
  delete(id) {
    return axios.delete(`${API_URL}/${id}`);
  }

  // Удалить все жанры
  deleteAll() {
    return axios.delete(API_URL);
  }

  // Поиск жанра по имени
  search(name) {
    return axios.get(`${API_URL}?name=${encodeURIComponent(name)}`);
  }
}

// Создаем экземпляр класса и экспортируем его
const genreServiceInstance = new GenreService();
export default genreServiceInstance;
