import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("http://localhost:2000/api/auth/login", { username, password })
      .then(response => {
        onLogin(); // Обновляем состояние в App при успешном входе
        navigate("/api"); // Перенаправляем на главную страницу
      })
      .catch(error => {
        setError("Неверное имя пользователя или пароль");
      });
  };

  // Обработчик для авторизации через Google
  const handleGoogleLogin = () => {
    // Открываем новое окно с маршрутом для авторизации через Google
    window.open("http://localhost:2000/api/auth/oauth/google", "_self");
  };

  const handleFacebookLogin = () => {
    // Открываем новое окно с маршрутом для авторизации через Google
    window.open("http://localhost:2000/api/auth/oauth/facebook", "_self");
  };

  return (
    <div>
      <h2>Вход</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="add-button" type="submit">Войти</button>
      </form>

      <button className="sort-button" onClick={handleGoogleLogin}>Войти через Google</button>
      <button className="sort-button" onClick={handleFacebookLogin}>Войти через Facebook</button>
    </div>
  );
}

export default Login;
